import React, { Component } from 'react';

import Leap from 'leapjs';
import 'leapjs-plugins'

export default class Cursor extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            position: {
                x: 0,
                y: 0,
            },
        };
    }
    componentDidMount() {
        this.leapController = new Leap.Controller();
        this.leapController.use('screenPosition', {scale: 0.6, positioning: 'absolute'});
        this.leapController.on('frame', (frame) => this.onLeapFrame(frame));
        this.leapController.connect();
        // console.log('mounting...');
        // Leap.loop({
        //     hand: function(hand) {
        //         console.log(hand.screenPosition());
        //     }
        // }).use('screenPosition');
    }
    componentDidUpdate() {

    }
    onLeapFrame(frame) {
        if (frame.hands.length > 0) {
            const hand = frame.hands[0];
            const handPosition = hand.screenPosition();
            // console.log(handPosition);
            const newPosition = {x: handPosition[0] - 300, y: handPosition[1]}
            this.setState({position: {x: handPosition[0] - 300, y: handPosition[1]}});
        }
    }
    render() {
        console.log("rendering...");
        return (
            <div className='Cursor-cursor' style={{left: `${this.state.position.x}px`, top: `${this.state.position.y}px`}}>

            </div>
        )
    }
}
