import React, { Component } from 'react';

import Canvas from './Canvas';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

export const CanvasHook = (component) => {
    return (props) => {
        return <component SpeechRecognition={SpeechRecognition} useSpeechRecognition={useSpeechRecognition} {...props} />;
    };
}
