import logo from './logo.svg';
import React, { Component } from 'react';
import './App.css';

import Cursor from './components/Cursor';
import Entry from './components/Entry';
import Canvas from './components/Canvas';
import Drawer from './components/Drawer';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isIndexPoint: false,
      handPos: {x: 0, y: 0},
      isGrabbing: false,
      isCircular: false,
      isSwipe: false,
    };
  }

  componentDidMount() {

  }
  componentDidUpdate() {

  }
  onPointUpdate(isIndexPoint, handPos) {
    this.setState({
      isIndexPoint: isIndexPoint,
      handPos: handPos,
    });
  }
  onHandGrabUpdate(isGrabbing) {
    this.setState({
      isGrabbing: isGrabbing,
    });
  }
  onCircularUpdate(isCircular) {
    this.setState({
      isCircular: isCircular,
    });
  }
  onSwipeUpdate(isSwipe) {
    this.setState({
      isSwipe: isSwipe,
    });
  }
  render() {
    return (
      <div>
        <Cursor onHandGrabUpdate={(isGrabbing) => this.onHandGrabUpdate(isGrabbing)} 
                onPointUpdate={(isIndexPoint, handPos) => this.onPointUpdate(isIndexPoint, handPos)}
                onCircularUpdate={(isCircular) => this.onCircularUpdate(isCircular)}
                onSwipeUpdate={(isSwipe) => this.onSwipeUpdate(isSwipe)}/>
        <Drawer isIndexPoint={this.state.isIndexPoint} handPos={this.state.handPos} isCircular={this.state.isCircular} isSwipe={this.state.isSwipe}/>
      </div>
    )
  }
}
