import React, { Component } from 'react';
import './Cursor.css';

import Leap from 'leapjs';
import 'leapjs-plugins'

export default class Cursor extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isGrabbing: false,
            position: {
                x: 0,
                y: 0,
            },
        };
        this.grabThreshold = 0.8;
        this.flatThreshold = 0.3;
    }
    componentDidMount() {
        this.leapController = new Leap.Controller();
        this.leapController.use('screenPosition', {scale: 0.6, positioning: 'absolute'});
        this.leapController.on('frame', (frame) => this.onLeapFrame(frame));
        this.leapController.connect();
    }
    componentDidUpdate() {

    }
    onLeapFrame(frame) {
        if (frame.hands.length > 0) {
            const hand = frame.hands[0];
            const handPosition = hand.screenPosition();
            this.setState({position: {x: handPosition[0] + 200, y: handPosition[1] + 300}});
            if (hand.grabStrength > this.grabThreshold) {
                if (!this.state.isGrabbing) {
                    this.setState({
                        isGrabbing: true,
                    });
                }
                this.props.onHandGrab();
            } else if (hand.grabStrength < this.flatThreshold) {
                if (this.state.isGrabbing) {
                    this.setState({
                        isGrabbing: false,
                    });
                }
            }
            if (hand.indexFinger.extended) {
                console.log('index finger is extended!');
            }
        }
    }
    render() {
        return (
            <div className={`Cursor-cursor${this.state.isGrabbing ? ' Cursor-cursorGrab' : ''}`} style={{left: `${this.state.position.x}px`, top: `${this.state.position.y}px`}}/>
        )
    }
}
