import React, { Component } from 'react';
import './Drawer.css';

import Canvas from './Canvas';
import DrawCard from './DrawCard';
import DrawHelper from './DrawHelper';

import { drawStates } from '../helpers/constants';

export default class Drawer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            drawState: drawStates.choosingState,
            isDrawing: false,
        };
    }
    componentDidMount() {

    }
    componentDidUpdate() {

    }
    updateIsDrawing(isDrawing) {
        if (this.state.drawState === drawStates.choosingState) {
            // transitioning from choosing to drawing, set up speech
            setupSpeech((transcript) => this.processSpeech(transcript));
        }
        this.setState({
            drawState: drawStates.drawingState,
            isDrawing: isDrawing,
        });
    }
    processSpeech(transcript) {
        console.log(transcript);
    }
    render() {
        return (
            <div className='Drawer-container'>
                <DrawCard swipeObject={this.props.swipeObject} isDrawing={this.state.isDrawing}/>
                <Canvas isIndexPoint={this.props.isIndexPoint} 
                        handPos={this.props.handPos} 
                        isCircular={this.props.isCircular}
                        onStartDraw={() => this.updateIsDrawing(true)} 
                        onEndDraw={() => this.updateIsDrawing(false)} />
                <DrawHelper drawState={this.state.drawState} />
            </div>
        );
    }
}
