import './Entry.css';

import SmallButton from './SmallButton';
import JoinCreate from './JoinCreate';

export default class Entry extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            didPressStart: false,
            roomName: "",
            isCreator: false,
        };
    }
    componentDidMount() {

    }
    componentDidUpdate() {

    }
    didClickStart() {
        console.log('started');
    }
    onJoinCreate(roomName, isCreator) {
        this.setState({
            roomName: roomName,
            isCreator: isCreator,
        });
    }
    render() {
        return (
            <div className='Entry-container'>
                <div className='Entry-firstLine'>
                    Multimodal
                </div>
