                </div>

                <div className='Entry-description'>
                    remote pictionary with your friends, done right
                </div>

                <div className='Entry-joinCreate'>
                    <JoinCreate handPos={this.state.handPos} 
                                isGrabbing={this.state.isGrabbing} 
                                socket={this.props.socket} 
                                onJoinCreate={(roomName, isCreator) => this.onJoinCreate(roomName, isCreator)} />
                </div>

            </div>
        )
    };
}
