import React, { Component } from 'react';

import Canvas from './Canvas';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

export default function CanvasHook(props) {
    const speechRecognition = (config) => useSpeechRecognition(config);
    const startListening = (config) => SpeechRecognition.startListening(config);
    return <Canvas startListening={(config) => startListening(config)} speechRecognition={(config) => speechRecognition(config)} {...props} />;
}
