import logo from './logo.svg';
import React, { Component } from 'react';
import './App.css';

import Cursor from './components/Cursor';
import Entry from './components/Entry';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isIndexPoint: false,
      isGrabbing: false,
    };
  }

  componentDidMount() {

  }
  componentDidUpdate() {

  }
  onIndexPoint() {
    this.setState({
      isIndexPoint: true,
    });
  }
  render() {
    return (
      <div>
        <Cursor onHandGrab={() => this.onHandGrab()} onIndexPoint={(handPos) => this.onIndexPoint()}/>
        <Canvas />
      </div>
    )
  }
}
