import logo from './logo.svg';
import React, { Component } from 'react';
import './App.css';

import Cursor from './components/Cursor';
import Entry from './components/Entry';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isIndexPoint: false,
      handPos: {x: 0, y:0},
      isGrabbing: false,
    };
  }

  componentDidMount() {

  }
  componentDidUpdate() {

  }
  onPointUpdate(isIndexPoint, handPos) {
    this.setState({
      isIndexPoint: isIndexPoint,
      handPos: handPos,
    });
  }
  onHandGrabUpdate() {
    this.setState({
      isGrabbing: true,
    });
  }
  render() {
    return (
      <div>
        <Cursor onHandGrabUpdate={() => this.onHandGrabUpdate()} onPointUpdate={(isIndexPoint, handPos) => this.onPointUpdate(isIndexPoint, handPos)}/>
        <Canvas />
      </div>
    )
  }
}
