import Leap from 'leapjs';

export const startLeapLoop = () => {
    console.log('starting leap loop!');
    Leap.loop({ hand: function(hand) {
        var handPosition = hand.screenPosition();
        var cursorPosition = [handPosition[0] - 300, handPosition[1]];
        console.log(hand);
        console.log(handPosition);
    }})
}
